import '../../css/dashboard.css';
import logo from '../../logo.svg';
import { useState, useRef, useEffect } from 'react';
import L from 'leaflet';

const ChatMsg = (props) => {
  return (
    <div className='msg' id={props.id}>
      <img src={'add image component here'} alt='Avatar' />
      <div className='text'>
        {props.text}
      </div>
    </div>
  )
}

const Dashboard = () => {

  const [log, setLog] = useState([
    {
      text: 'Car theft on 123 Avenue at 1:23 am',
    },
  ])

  const scroll = useRef()
  useEffect(() => {
    scroll.current.scrollIntoView(false)
  }, [log]);

  async function getAlert(prompt) {

  }

  async function getVisual(prompt) {
    try {
      const response = await fetch('/Users/cadenpun/Documents/GitHub/sfhacks24/client/src/testdata.csv');
      const csvText = await response.text();

      const data = csvText
        .trim()
        .split('\n')
        .map(row => row.split(','))
        .slice(1)
        .filter(row => {
          const lat = parseFloat(row[0]);
          const lng = parseFloat(row[1]);
          return !isNaN(lat) && !isNaN(lng); // Filter out rows with NaN latitude or longitude
        });

      if (data.length === 0) {
        throw new Error('No valid data found.');
      }

      const sumLat = data.reduce((acc, val) => acc + parseFloat(val[0]), 0);
      const sumLng = data.reduce((acc, val) => acc + parseFloat(val[1]), 0);
      const meanLat = sumLat / data.length;
      const meanLng = sumLng / data.length;

      const map = L.map('map').setView([meanLat, meanLng], 12);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      }).addTo(map);

      L.heatLayer(data, { radius: 10 }).addTo(map);
    } catch (error) {
      console.error('Error fetching or processing CSV data:', error);
    }
  }   

  return (
    <div className='dashboard'>
      <div id='map'>
        {/* <img src={logo} alt='Placeholder' /> */}
      </div>

      <div className='chat'>
        <div className='versionheader'>
          Location Alerts
          <button onClick={() => getVisual()}>
            Generate Map
          </button>
        </div>

        <div className='logcontainer'>
          <div ref={scroll}>
            {log.map((msg) => (
              <div>
                <ChatMsg
                  id={'severe'}
                  text={msg.text}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
