import '../../css/dashboard.css';
import logo from '../../logo.svg';
import { useState, useRef, useEffect } from 'react';
import L from 'leaflet';

const ChatMsg = (props) => {
  return (
    <div className='msg' id={props.id}>
      <img src={'add image component here'} alt='Avatar' />
      <div className='text'>
        {props.text}
      </div>
    </div>
  )
}

const Dashboard = () => {

  const [log, setLog] = useState([
    {
      text: 'Car theft on 123 Avenue at 1:23 am',
    },
  ])

  const scroll = useRef()
  useEffect(() => {
    scroll.current.scrollIntoView(false)
  }, [log]);

  async function getAlert(prompt) {

  }

  async function getVisual(prompt) {
    document.getElementById('map').innerHTML = ''

    fetch('../../Police_Department_Incident_Reports__2018_to_Present_20240405-2.csv')
      .then(response => response.text())
      .then(csvText => {
        const data = csvText
          .trim()
          .split('\n')
          .map(row => row.split(','))
          .slice(1)
          .filter(row => !isNaN(parseFloat(row[0])) && !isNaN(parseFloat(row[1])))

        const sumLat = data.reduce((acc, val) => acc + parseFloat(val[0]), 0);
        const sumLng = data.reduce((acc, val) => acc + parseFloat(val[1]), 0);
        const meanLat = sumLat / data.length;
        const meanLng = sumLng / data.length;

        const map = L.map('map').setView([meanLat, meanLng], 12);

        L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
          attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        }).addTo(map);

        const heatmapLayer = L.heatLayer(data, { radius: 10 }).addTo(map);
      })
      .catch(error => console.error('Error fetching or processing CSV data:', error));

    // const graph = document.createElement('script')
    // //graph.innerHTML = `${}`
    // document.getElementById('map').appendChild(graph)
  }

  return (
    <div className='dashboard'>
      <div id='map'>
        <img src={logo} alt='Placeholder' />
      </div>

      <div className='chat'>
        <div className='versionheader'>
          Location Alerts
          <button onClick={() => getVisual()}>
            Map
          </button>
        </div>

        <div className='logcontainer'>
          <div ref={scroll}>
            {log.map((msg) => (
              <div>
                <ChatMsg
                  id={'severe'}
                  text={msg.text}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
