import '../../css/dashboard.css';
import logo from '../../logo.svg';
import { useState, useRef, useEffect } from 'react';
import L from 'leaflet';

const ChatMsg = (props) => {
  return (
    <div className='msg' id={props.id}>
      <img src={'add image component here'} alt='Avatar' />
      <div className='text'>
        {props.text}
      </div>
    </div>
  )
}

const Dashboard = () => {

  const [log, setLog] = useState([
    {
      text: 'Car theft on 123 Avenue at 1:23 am',
    },
  ])

  const scroll = useRef()
  useEffect(() => {
    scroll.current.scrollIntoView(false)
  }, [log]);

  async function getAlert(prompt) {

  }

  async function getVisual() {
    try {
      const response = await fetch('../../testdata.csv');
      const csvText = await response.text();
      console.log(csvText)

      const rows = csvText.trim().split('\n');
      const header = rows[0].split(',').map(col => col.trim()); // Assuming first row is header
      const latitudeIndex = header.indexOf('Latitude');
      const longitudeIndex = header.indexOf('Longitude');
      const data = rows.slice(1).map(row => row.split(',').map(col => col.trim()));

      // Filter out rows where latitude or longitude is NaN
      const filteredData = data.filter(row => {
        const lat = parseFloat(row[latitudeIndex]);
        const lng = parseFloat(row[longitudeIndex]);
        return !isNaN(lat) && !isNaN(lng);
      });
      if (filteredData.length === 0) {
        throw new Error('No valid data found.');
      }

      const sumLat = filteredData.reduce((acc, val) => acc + parseFloat(val[0]), 0);
      const sumLng = filteredData.reduce((acc, val) => acc + parseFloat(val[1]), 0);
      const meanLat = sumLat / filteredData.length;
      const meanLng = sumLng / filteredData.length;

      const map = L.map('map').setView([meanLat, meanLng], 12);

      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
      }).addTo(map);

      L.heatLayer(filteredData, { radius: 10 }).addTo(map);
    } catch (error) {
      console.error('Error fetching or processing CSV data:', error);
    }
  }

  return (
    <div className='dashboard'>
      <div id='map'>
        {/* <img src={logo} alt='Placeholder' /> */}
      </div>

      <div className='chat'>
        <div className='versionheader'>
          Location Alerts
          <button onClick={() => getVisual()}>
            Generate Map
          </button>
        </div>

        <div className='logcontainer'>
          <div ref={scroll}>
            {log.map((msg) => (
              <div>
                <ChatMsg
                  id={'severe'}
                  text={msg.text}
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
